  KEY `user_id` (`user_id`),
  KEY `product_id` (`product_id`),
  KEY `order_detail_id` (`order_detail_id`),
  CONSTRAINT `productreviews_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `account` (`id`),
  CONSTRAINT `productreviews_ibfk_2` FOREIGN KEY (`product_id`) REFERENCES `product` (`id`),
  CONSTRAINT `productreviews_ibfk_3` FOREIGN KEY (`order_detail_id`) REFERENCES `orderdetail` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8mb4;

DROP TABLE IF EXISTS `role`;
CREATE TABLE `role` (
  `code` varchar(255) NOT NULL,
  `name` varchar(255) NOT NULL,
